import React, { useState } from "react";
//hook
const App = () => {
  const [likecount, setlikecount] = useState(0);
  const [like, setlike] = useState(false);
  const [color, setColor] = useState("gray");

  const empty = () => {
    setlike(true);
  };

  const Colorschange = () => {
    setColor("navy");
    if (likecount === 1) {
      setlikecount(0);
      setColor("gray");
    } else {
      setlikecount(1);
      setColor("navy");
    }
  };

  return (
    <div>
      {like ? (
        <div>
          <button>Haha</button>
          <button>Angry</button>
        </div>
      ) : null}

      <button
        style={{ backgroundColor: color }}
        onClick={Colorschange}
        onMouseEnter={empty}
      >
        like
      </button>
      <p>{likecount}</p>
    </div>
  );
};
export default App;
